<script>
    let personalityType = "<?php echo $personality_type; ?>";  // Set dynamically based on selected personality
    let currentPage = 0;  // Track the current page in a section
    let currentSection = "Introduction";  // Default section

    // Get the PHP content and map it to JavaScript
    const personalitySections = <?php echo json_encode($personality_descriptions); ?>;
    let isNavigating = false;

    // Function to change the page in the current section (previous/next)
    function changePage(direction) {
        if (isNavigating) {
            return; // Prevent clicking while navigating
        }

        isNavigating = true;

        const sectionContent = personalitySections[personalityType]["sections"][currentSection];

        if (sectionContent && sectionContent.length > 0) {
            currentPage += direction;

            if (currentPage < 0) {
                currentPage = 0;
            }

            if (currentPage >= sectionContent.length) {
                currentPage = sectionContent.length - 1;
            }

            document.getElementById('description').innerHTML = sectionContent[currentPage];
            updateNavButtons(sectionContent.length);
        }

        setTimeout(() => {
            isNavigating = false;
        }, 300);
    }

    // Function to update the state of the navigation buttons (enable/disable)
    function updateNavButtons(totalPages) {
        const prevButton = document.getElementById('prev');
        const nextButton = document.getElementById('next');

        prevButton.disabled = currentPage === 0;
        nextButton.disabled = currentPage === totalPages - 1;
    }

    // Function to load content based on the selected section
    function changeContent(section) {
        currentSection = section;
        currentPage = 0;  // Reset to the first page when switching sections

        const sectionContent = personalitySections[personalityType]["sections"][currentSection];

        if (sectionContent && sectionContent.length > 0) {
            document.getElementById('description').innerHTML = sectionContent[currentPage];
        }

        document.getElementById('current-section').innerText = section;
        updateNavButtons(sectionContent.length);
    }

    // Initialize the personality type's title and first section content
    document.addEventListener("DOMContentLoaded", function() {
        document.getElementById('personality-title').innerText = personalitySections[personalityType]["title"];
        changeContent("Introduction");  // Start by showing the "Introduction" section

        // Attach changeContent to the sidebar items (this assumes you have a sidebar to select sections)
        document.querySelectorAll('.sidebar ul li').forEach(item => {
            item.addEventListener('click', function() {
                changeContent(this.getAttribute('data-section'));
            });
        });
    });
</script>

